    this.action = "Nouveau produit";
    this.productForm.controls.name.setValue("");
    this.productForm.controls.category.setValue("");
    this.productForm.controls.quantity.setValue("");
    this.productForm.controls.price.setValue("");
    this.formModal.show();
  }

